package com.alex.yang.weather.data.utils

import okhttp3.Interceptor
import okhttp3.MediaType.Companion.toMediaTypeOrNull
import okhttp3.Protocol
import okhttp3.Response
import okhttp3.ResponseBody.Companion.toResponseBody
import java.io.IOException

/**
 * Created by AlexYang on 2025/10/16.
 *
 * 測試假資料
 */
class MockServerInterceptor : Interceptor {
    private val sMediaType = "application/json".toMediaTypeOrNull()

    @Throws(IOException::class)
    override fun intercept(chain: Interceptor.Chain): Response {
        val builder = Response.Builder()
            .code(200)
            .request(chain.request())
            .protocol(Protocol.HTTP_1_1)
            .addHeader("Content-Type", "application/json; charset=utf-8")
        return chain.request().url.toUri().path.run {
            when {
                contains("/VisualCrossingWebServices/rest/services/timeline/") ->
                    builder
                        .message(MOCK_WEATHER_JSON)
                        .body(MOCK_WEATHER_JSON.toByteArray().toResponseBody(sMediaType)).build()
                else -> chain.proceed(chain.request())
            }
        }
    }
}

